using ImageComparison;
using NUnit.Framework;
using System;
using System.Drawing;
using System.IO;

namespace ImageComparisonTests
{
    [TestFixture]
    public class ScreenCaptureTests
    {
        ///<remarks>
        ///To convert an image to base 64 string, please visit the following:
        /// https://www.base64-image.de/
        /// </remarks>

        private const string CHECKERBOARD_IMAGE = "";
        private const string RED_CHECKERBOARD_IMAGE_HEIGHT_DIFF = "";

        [Author("Hunter")]
        [Test]
        [TestCase(CHECKERBOARD_IMAGE, true, TestName = "VerifyGetImageDifference(NormalCheckerboard)")]
        [TestCase(RED_CHECKERBOARD_IMAGE_HEIGHT_DIFF, false, TestName = "VerifyGetImageDifference(RedCheckerboard)")]
        [Description("Verifies get image difference behaves as expected.")]
        public void VerifyGetImageDifference(string comparisonImageAsBase64, bool areImagesSame)
        {
            byte[] baseImageAsByteArray = Convert.FromBase64String(CHECKERBOARD_IMAGE);
            byte[] comparisonImageAsByteArray = Convert.FromBase64String(comparisonImageAsBase64);

            Image baseImage = ConvertByteArrayToImage(baseImageAsByteArray);
            Image comparisonImage = ConvertByteArrayToImage(comparisonImageAsByteArray);

            try
            {
                if (areImagesSame)
                {
                    Assert.That(ScreenCapture.GetImageDifference((Bitmap)baseImage, (Bitmap)comparisonImage), Is.EqualTo(0.0d));
                }
                else
                {
                    Assert.Throws<InvalidOperationException>
                        (() => ScreenCapture.GetImageDifference((Bitmap)baseImage, (Bitmap)comparisonImage));
                }
            }
            finally
            {
                DisposeOfImage(baseImage);
                DisposeOfImage(comparisonImage);
            }
        }

        [Author("Hunter")]
        [Test]
        [Description("Verify null image checks throw exceptions.")]
        public void VerifyNullImageComparison()
        {
            Assert.Throws<ArgumentException>(() => ScreenCapture.GetImageDifference(new Bitmap(0, 0), new Bitmap(0, 0)));
        }

        //Parameters: upper left point, lower right point, will it throw an exception
        static object[] _captureBitmapTests =
        {            
            new object[] { new Point(0, 0), new Point(-1, 1), true },
            new object[] { new Point(0, 0), new Point(1, 1), false },
        };

        [Author("Hunter")]
        [Test]
        [TestCaseSource(nameof(_captureBitmapTests))]
        [Description("Verifies bounds of Rectangles when using CaptureBitmapImage()")]
        public void VerifyCaptureBitmapImage(Point topLeftPoint, Point lowerRightPoint, bool throwException)
        {
            if (throwException)
            {
                Assert.Throws<InvalidOperationException>(() => ScreenCapture.CaptureBitmapImage(topLeftPoint, lowerRightPoint));
            }
            else
            {
                Image image = null;
                try
                {
                    image = ScreenCapture.CaptureBitmapImage(topLeftPoint, lowerRightPoint);
                    Assert.That(image, Is.Not.Null);
                }
                finally
                {
                    DisposeOfImage(image);
                }                
            }
        }

        #region Private Methods

        private Image ConvertByteArrayToImage(byte[] array)
        {
            using (MemoryStream stream = new MemoryStream(array))
            {
                return Image.FromStream(stream);
            }
        }

        private void DisposeOfImage(Image image)
        {
            if (image != null)
            {
                image.Dispose();
            }
        }

        #endregion
    }
}